  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/db.ts.content:
import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

let _pool: Pool | null = null;
let _db: ReturnType<typeof drizzle> | null = null;

function initializeDatabase() {
  if (!process.env.DATABASE_URL) {
    throw new Error(
      "DATABASE_URL must be set. Did you forget to provision a database?",
    );
  }

  if (!_pool) {
    _pool = new Pool({ connectionString: process.env.DATABASE_URL });
  }

  if (!_db) {
    _db = drizzle({ client: _pool, schema });
  }

  return { pool: _pool, db: _db };
}

export const getPool = () => {
  const { pool } = initializeDatabase();
  return pool;
};

export const getDb = () => {
  const { db } = initializeDatabase();
  return db;
};

// For backward compatibility, export a getter for db
export const db = new Proxy({} as ReturnType<typeof drizzle>, {
  get(target, prop) {
    const dbInstance = getDb();
    return (dbInstance as any)[prop];
  }
});

export const pool = new Proxy({} as Pool, {
  get(target, prop) {
    const poolInstance = getPool();
    return (poolInstance as any)[prop];
  }
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      // Use console.log directly to avoid importing vite module in production
      const formattedTime = new Date().toLocaleTimeString("en-US", {
        hour: "numeric",
        minute: "2-digit",
        second: "2-digit",
        hour12: true,
      });
      console.log(`${formattedTime} [express] ${logLine}`);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    const { setupVite } = await import("./vite.js");
    await setupVite(app, server);
  } else {
    const { serveStatic } = await import("./vite.js");
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    const formattedTime = new Date().toLocaleTimeString("en-US", {
      hour: "numeric",
      minute: "2-digit", 
      second: "2-digit",
      hour12: true,
    });
    console.log(`${formattedTime} [express] serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./auth";
import { prowlerService } from "./services/prowlerService";
import { prowlerConfigurationSchema } from "@shared/schema";
import { z } from "zod";
import bcrypt from "bcrypt";

// Admin middleware
const isAdmin: typeof isAuthenticated = async (req, res, next) => {
  if (!req.isAuthenticated()) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const user = await storage.getUser((req.user as any).id);
  if (!user || user.role !== "admin") {
    return res.status(403).json({ message: "Admin access required" });
  }

  return next();
};

// Validation schemas for user management
const createUserSchema = z.object({
  username: z.string().min(1, "Username is required").min(3, "Username must be at least 3 characters"),
  email: z.string().min(1, "Email is required").email("Must be a valid email"),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  password: z.string().min(1, "Password is required").min(6, "Password must be at least 6 characters"),
  role: z.enum(["admin", "user"]).default("user"),
});

const updateUserSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters").optional(),
  email: z.string().email("Must be a valid email").optional(),
  firstName: z.string().min(1, "First name is required").optional(),
  lastName: z.string().min(1, "Last name is required").optional(),
  password: z.string().min(6, "Password must be at least 6 characters").optional(),
  role: z.enum(["admin", "user"]).optional(),
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Health check endpoint
  app.get('/api/health', (req, res) => {
    res.status(200).json({ 
      status: 'ok', 
      timestamp: new Date().toISOString(),
      uptime: process.uptime()
    });
  });

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      // Don't send password hash to client
      const { passwordHash, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Prowler configuration routes
  app.get('/api/prowler/configuration', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const config = await storage.getProwlerConfiguration(userId);

      if (!config) {
        return res.json(null);
      }

      // Don't send password hash to client
      const { prowlerPasswordHash, ...configWithoutPassword } = config;
      res.json(configWithoutPassword);
    } catch (error) {
      console.error("Error fetching Prowler configuration:", error);
      res.status(500).json({ message: "Failed to fetch configuration" });
    }
  });

  app.post('/api/prowler/configuration', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const validatedData = prowlerConfigurationSchema.parse(req.body);

      // Hash the password
      const prowlerPasswordHash = await prowlerService.hashPassword(validatedData.prowlerPassword);

      const config = await storage.upsertProwlerConfiguration(userId, {
        prowlerUrl: validatedData.prowlerUrl,
        prowlerEmail: validatedData.prowlerEmail,
        prowlerPasswordHash,
      });

      // Don't send password hash to client
      const { prowlerPasswordHash: _, ...configWithoutPassword } = config;
      res.json(configWithoutPassword);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      console.error("Error saving Prowler configuration:", error);
      res.status(500).json({ message: "Failed to save configuration" });
    }
  });

  app.post('/api/prowler/test-connection', isAuthenticated, async (req: any, res) => {
    try {
      const validatedData = prowlerConfigurationSchema.parse(req.body);

      const result = await prowlerService.testConnection(
        validatedData.prowlerUrl,
        validatedData.prowlerEmail,
        validatedData.prowlerPassword
      );

      res.json(result);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      console.error("Error testing Prowler connection:", error);
      res.status(500).json({ message: "Failed to test connection" });
    }
  });

  // User management routes
  app.get('/api/users', isAdmin, async (req: any, res) => {
    try {
      const users = await storage.getAllUsers();
      // Don't send password hashes to client
      const usersWithoutPasswords = users.map(user => {
        const { passwordHash, ...userWithoutPassword } = user;
        return userWithoutPassword;
      });
      res.json(usersWithoutPasswords);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.post('/api/users', isAdmin, async (req: any, res) => {
    try {
      const validatedData = createUserSchema.parse(req.body);

      // Check if username or email already exists
      const existingUserByUsername = await storage.getUserByUsername(validatedData.username);
      if (existingUserByUsername) {
        return res.status(400).json({ message: "Username already exists" });
      }

      const existingUserByEmail = await storage.getUserByEmail(validatedData.email);
      if (existingUserByEmail) {
        return res.status(400).json({ message: "Email already exists" });
      }

      // Hash the password
      const passwordHash = await bcrypt.hash(validatedData.password, 10);

      const user = await storage.createUser({
        username: validatedData.username,
        email: validatedData.email,
        firstName: validatedData.firstName,
        lastName: validatedData.lastName,
        passwordHash,
        role: validatedData.role,
      });

      // Don't send password hash to client
      const { passwordHash: _, ...userWithoutPassword } = user;
      res.status(201).json(userWithoutPassword);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      console.error("Error creating user:", error);
      res.status(500).json({ message: "Failed to create user" });
    }
  });

  app.put('/api/users/:id', isAdmin, async (req: any, res) => {
    try {
      const { id } = req.params;
      const validatedData = updateUserSchema.parse(req.body);

      // Check if user exists
      const existingUser = await storage.getUser(id);
      if (!existingUser) {
        return res.status(404).json({ message: "User not found" });
      }

      // If username is being updated, check if it's already taken by another user
      if (validatedData.username && validatedData.username !== existingUser.username) {
        const userWithSameUsername = await storage.getUserByUsername(validatedData.username);
        if (userWithSameUsername && userWithSameUsername.id !== id) {
          return res.status(400).json({ message: "Username already exists" });
        }
      }

      // If email is being updated, check if it's already taken by another user
      if (validatedData.email && validatedData.email !== existingUser.email) {
        const userWithSameEmail = await storage.getUserByEmail(validatedData.email);
        if (userWithSameEmail && userWithSameEmail.id !== id) {
          return res.status(400).json({ message: "Email already exists" });
        }
      }

      // Prepare update data
      const updateData: any = { ...validatedData };

      // Hash password if provided
      if (validatedData.password) {
        updateData.passwordHash = await bcrypt.hash(validatedData.password, 10);
        delete updateData.password;
      }

      const user = await storage.updateUser(id, updateData);

      // Don't send password hash to client
      const { passwordHash: _, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      console.error("Error updating user:", error);
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  app.delete('/api/users/:id', isAdmin, async (req: any, res) => {
    try {
      const { id } = req.params;

      // Check if user exists
      const existingUser = await storage.getUser(id);
      if (!existingUser) {
        return res.status(404).json({ message: "User not found" });
      }

      // Prevent deletion of current user
      if (req.user.id === id) {
        return res.status(400).json({ message: "Cannot delete your own account" });
      }

      await storage.deleteUser(id);
      res.json({ message: "User deleted successfully" });
    } catch (error) {
      console.error("Error deleting user:", error);
      res.status(500).json({ message: "Failed to delete user" });
    }
  });

  // Asset/Resource routes
  app.get('/api/assets', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const config = await storage.getProwlerConfiguration(userId);

      if (!config) {
        return res.status(404).json({ message: "No Prowler configuration found" });
      }

      const { resourceType, status, severity, search } = req.query;

      const assets = await storage.getAssets(config.id, {
        resourceType: resourceType as string,
        status: status as string,
        severity: severity as string,
        search: search as string,
      });

      res.json(assets);
    } catch (error) {
      console.error("Error fetching assets:", error);
      res.status(500).json({ message: "Failed to fetch assets" });
    }
  });

  app.get('/api/assets/stats', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const config = await storage.getProwlerConfiguration(userId);

      if (!config) {
        return res.status(404).json({ message: "No Prowler configuration found" });
      }

      const stats = await storage.getAssetStats(config.id);
      res.json(stats);
    } catch (error) {
      console.error("Error fetching asset stats:", error);
      res.status(500).json({ message: "Failed to fetch asset stats" });
    }
  });

  app.post('/api/assets/sync', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const config = await storage.getProwlerConfiguration(userId);

      if (!config) {
        return res.status(404).json({ message: "No Prowler configuration found" });
      }

      // Note: In production, we would need to decrypt the stored password
      // For now, we'll return an error asking user to reconfigure
      res.status(400).json({ 
        message: "Please reconfigure your Prowler connection to sync data",
        requiresReconfiguration: true 
      });
    } catch (error) {
      console.error("Error syncing assets:", error);
      res.status(500).json({ message: "Failed to sync assets" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  prowlerConfigurations,
  assets,
  type User,
  type UpsertUser,
  type ProwlerConfiguration,
  type InsertProwlerConfiguration,
  type Asset,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc } from "drizzle-orm";

// Interface for storage operations
export interface IStorage {
  // User operations
  // User operations for local authentication.
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getAllUsers(): Promise<User[]>;
  upsertUser(user: UpsertUser): Promise<User>;
  createUser(user: UpsertUser): Promise<User>;
  updateUser(id: string, user: Partial<UpsertUser>): Promise<User>;
  deleteUser(id: string): Promise<void>;

  // Prowler configuration operations
  getProwlerConfiguration(userId: string): Promise<ProwlerConfiguration | undefined>;
  upsertProwlerConfiguration(userId: string, config: InsertProwlerConfiguration & { prowlerPasswordHash: string }): Promise<ProwlerConfiguration>;
  updateConfigurationStatus(configId: string, status: "connected" | "disconnected" | "error", lastSyncAt?: Date): Promise<void>;

  // Asset operations
  getAssets(configurationId: string, filters?: {
    resourceType?: string;
    status?: string;
    severity?: string;
    search?: string;
  }): Promise<Asset[]>;
  upsertAssets(configurationId: string, assets: Array<Omit<Asset, 'id' | 'configurationId' | 'createdAt' | 'updatedAt'>>): Promise<void>;
  getAssetStats(configurationId: string): Promise<{
    totalResources: number;
    criticalIssues: number;
    compliantResources: number;
    lastScan: Date | null;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  // User operations for local authentication.

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async createUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .returning();
    return user;
  }

  async getAllUsers(): Promise<User[]> {
    const allUsers = await db.select().from(users).orderBy(desc(users.createdAt));
    return allUsers;
  }

  async updateUser(id: string, userData: Partial<UpsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        ...userData,
        updatedAt: new Date(),
      })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async deleteUser(id: string): Promise<void> {
    await db.delete(users).where(eq(users.id, id));
  }

  // Prowler configuration operations
  async getProwlerConfiguration(userId: string): Promise<ProwlerConfiguration | undefined> {
    const [config] = await db
      .select()
      .from(prowlerConfigurations)
      .where(and(eq(prowlerConfigurations.userId, userId), eq(prowlerConfigurations.isActive, true)));
    return config;
  }

  async upsertProwlerConfiguration(userId: string, config: InsertProwlerConfiguration & { prowlerPasswordHash: string }): Promise<ProwlerConfiguration> {
    // First, deactivate any existing configurations
    await db
      .update(prowlerConfigurations)
      .set({ isActive: false, updatedAt: new Date() })
      .where(eq(prowlerConfigurations.userId, userId));

    // Insert new configuration
    const [newConfig] = await db
      .insert(prowlerConfigurations)
      .values({
        userId,
        prowlerUrl: config.prowlerUrl,
        prowlerEmail: config.prowlerEmail,
        prowlerPasswordHash: config.prowlerPasswordHash,
        isActive: true,
      })
      .returning();

    return newConfig;
  }

  async updateConfigurationStatus(configId: string, status: "connected" | "disconnected" | "error", lastSyncAt?: Date): Promise<void> {
    await db
      .update(prowlerConfigurations)
      .set({ 
        connectionStatus: status, 
        lastSyncAt: lastSyncAt || new Date(),
        updatedAt: new Date() 
      })
      .where(eq(prowlerConfigurations.id, configId));
  }

  // Asset operations
  async getAssets(configurationId: string, filters?: {
    resourceType?: string;
    status?: string;
    severity?: string;
    search?: string;
  }): Promise<Asset[]> {
    const conditions = [eq(assets.configurationId, configurationId)];

    if (filters?.resourceType) {
      conditions.push(eq(assets.resourceType, filters.resourceType));
    }
    if (filters?.status) {
      conditions.push(eq(assets.status, filters.status as any));
    }
    if (filters?.severity) {
      conditions.push(eq(assets.severity, filters.severity as any));
    }

    const results = await db
      .select()
      .from(assets)
      .where(and(...conditions))
      .orderBy(desc(assets.updatedAt));

    if (filters?.search) {
      return results.filter(asset => 
        asset.resourceName.toLowerCase().includes(filters.search!.toLowerCase()) ||
        asset.resourceId.toLowerCase().includes(filters.search!.toLowerCase())
      );
    }

    return results;
  }

  async upsertAssets(configurationId: string, assetsData: Array<Omit<Asset, 'id' | 'configurationId' | 'createdAt' | 'updatedAt'>>): Promise<void> {
    // Clear existing assets for this configuration
    await db.delete(assets).where(eq(assets.configurationId, configurationId));

    // Insert new assets
    if (assetsData.length > 0) {
      await db.insert(assets).values(
        assetsData.map(asset => ({
          ...asset,
          configurationId,
        }))
      );
    }
  }

  async getAssetStats(configurationId: string): Promise<{
    totalResources: number;
    criticalIssues: number;
    compliantResources: number;
    lastScan: Date | null;
  }> {
    const allAssets = await db.select().from(assets).where(eq(assets.configurationId, configurationId));

    const totalResources = allAssets.length;
    const criticalIssues = allAssets.filter(asset => asset.severity === 'critical').length;
    const compliantResources = allAssets.filter(asset => asset.status === 'compliant').length;
    const lastScan = allAssets.length > 0 
      ? new Date(Math.max(...allAssets.map(asset => new Date(asset.lastCheckedAt || asset.updatedAt!).getTime())))
      : null;

    return {
      totalResources,
      criticalIssues,
      compliantResources,
      lastScan,
    };
  }
}

export const storage = new DatabaseStorage();

